package handler

import (
	"bytes"
	"fmt"
	"net/http"
	"os/exec"

	"github.com/julienschmidt/httprouter"
)

var base64 = ""

// Index used to test server
func Index(w http.ResponseWriter, r *http.Request, _ httprouter.Params) {

	// cat lampBase64.txt | base64 -d | pngquant 256 | >> kkk.png

	c := fmt.Sprintf("echo %s | base64 -d | pngquant 256 | base64", base64)

	var stdout, stderr bytes.Buffer

	cmd := exec.Command("bash", "-c", c)
	cmd.Stdout = &stdout
	cmd.Stderr = &stderr

	err := cmd.Start()
	if err != nil {
		fmt.Println(err)
	}

	err = cmd.Wait()
	if err != nil {
		fmt.Println(err)
	}
	fmt.Println(stdout.String(), stderr.String())

}
